    private void dividendStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_dividendStateChanged
        // TODO add your handling code here:
        int sumSalary =0;
        if(salaryYear.isSelected())
        {
           sumSalary = (Integer) salary.getValue();
        }
        else
        {
           sumSalary = ((Integer) salary.getValue())*12;
        }
        int sumBonus = (Integer) bonus.getValue();
        int sumDividend = (Integer) dividend.getValue();
        int sumAnotherIncome = (Integer) anotherIncome.getValue();
        int SUMIncome = sumSalary + sumBonus + sumDividend + sumAnotherIncome;
        sumIncome.setText (String.valueOf(SUMIncome));
    }//GEN-LAST:event_dividendStateChanged

