    private void outStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_outStateChanged
        // TODO add your handling code here:
        int SUMIncome = Integer.parseInt(sumIncome.getText());
        int OUT = (Integer) out.getValue();
        if(OUT >= SUMIncome/2)
        {
            OUT = SUMIncome/2;
        }
        
        if(OUT >= 100000)
        {
            OUT = 100000;
        }
        
        out.setValue(OUT);
    }//GEN-LAST:event_outStateChanged

