    private void donateEduStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_donateEduStateChanged
        // TODO add your handling code here:
        int SUMIncome = Integer.parseInt(sumIncome.getText());
        int a= (Integer)out.getValue();
        int b =(Integer)self.getValue();
        int c = (Integer)marry.getValue();
        int d =(Integer)childInMoney.getValue();
        int e = (Integer)parentMoney.getValue();
        int f=(Integer)life.getValue();
        int g =(Integer)health.getValue();
        int h =(Integer)social.getValue();
        int i =(Integer)RMF.getValue();
        int j =(Integer)LTF.getValue();
        int k =(Integer)travelIn.getValue();
        int l = (Integer)buyHouse.getValue();
        int m = (Integer)buyIn.getValue(); 
        int SUMReduce = a+b+c+d+e+f+g+h+i+j+k+l+m;
        
        int edu = (Integer)donateEdu.getValue();
        int don = (SUMIncome-SUMReduce)/10;
        if((edu*2)>don)
        {
            donateJing.setValue(don);
        }
        else
        {
            donateJing.setValue(edu*2);
        }

 
    }//GEN-LAST:event_donateEduStateChanged

