    private void jTabbedPane2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTabbedPane2MouseClicked
        // TODO add your handling code here:
        int SUMIncome = Integer.parseInt(sumIncome.getText());
        int a= (Integer)out.getValue();
        int b =(Integer)self.getValue();
        int c = (Integer)marry.getValue();
        int d =(Integer)childInMoney.getValue();
        int e = (Integer)parentMoney.getValue();
        int f=(Integer)life.getValue();
        int g =(Integer)health.getValue();
        int h =(Integer)social.getValue();
        int i =(Integer)RMF.getValue();
        int j =(Integer)LTF.getValue();
        int k =(Integer)travelIn.getValue();
        int l = (Integer)buyHouse.getValue();
        int m = (Integer)buyIn.getValue(); 
        int n = (Integer)donateJing.getValue();
        int o = (Integer)donate.getValue();
        int SUMReduce = a+b+c+d+e+f+g+h+i+j+k+l+m+n+o;
        
        int netIncome = SUMIncome-SUMReduce;
        netSum.setText (String.valueOf(netIncome));
        
        int tang = netIncome;
        double tax = 0.0;
        double taxcal = 0.0;
        if (tang > 5000000)
        {
        //35%
        taxcal = (tang-5000000)*0.35;
        tax = tax + taxcal;
        tang = 5000000;
            
        }
        if (tang > 2000000)
        {
            //30%
            taxcal = (tang-2000000)*0.3;
            tax = tax + taxcal;
            tang = 2000000;

        }
        if (tang > 1000000)
        {
            //25%
             taxcal = (tang-1000000)*0.25;
             tax = tax + taxcal;
             tang = 1000000;

        }
        if (tang > 750000)
        {
            //20%
            taxcal = (tang-750000)*0.2;
            tax = tax + taxcal;
            tang = 750000;

        }
        if (tang > 500000)
        {
            //15%
            taxcal = (tang-500000)*0.15;
            tax = tax + taxcal;
            tang = 500000;
              
        }
        if (tang > 300000)
        {
            //10%
            taxcal = (tang-300000)*0.1;
            tax = tax + taxcal;
            tang = 300000;

        }
         if (tang > 150000)
        {
             //5%
            taxcal = (tang-150000)*0.05;
            tax = tax + taxcal;
            tang = 150000;

        }
          
        int TBF=(Integer)taxbefore.getValue();
        double finaltax = tax-TBF;
        if (finaltax >=0)
        {
            taxPlus.setText (String.valueOf(finaltax));
        }
        else if (finaltax <0)
        {
            taxRe.setText (String.valueOf(Math.abs(finaltax)));
        }
        
    }//GEN-LAST:event_jTabbedPane2MouseClicked

